package projectAssiment;

public class pattern44 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<6;i++)
		{
			for(int j=0;j<5;j++)
			{
				if(j+i>=5) 
					System.out.print(i+" ");
				else
					System.out.print(" ");
			}
			System.out.println();

		}

	}

}
